 
-- Add/modify columns 
alter table CD_PRINT_TEMPLATE add version FLOAT;
-- Add comments to the columns 
comment on column CD_PRINT_TEMPLATE.version
  is '套打版本';

-- Add/modify columns 
alter table CD_PRINT_TEMPLATE add print_template VARCHAR2(64 CHAR);
-- Add comments to the columns 
comment on column CD_PRINT_TEMPLATE.print_template
  is '套打内容';
  
update cd_print_template t set t.version = 1 where 1 = 1;

-- Create table
create table CD_PRINT_CONTENTS
(
  uuid          VARCHAR2(255 CHAR) not null,
  create_time   TIMESTAMP(6),
  creator       VARCHAR2(255 CHAR),
  modifier      VARCHAR2(255 CHAR),
  modify_time   TIMESTAMP(6),
  rec_ver       NUMBER(10),
  lang          VARCHAR2(64 CHAR),
  remark        VARCHAR2(2014 CHAR),
  file_uuid     VARCHAR2(64 CHAR),
  sort_order    NUMBER(2),
  template_uuid VARCHAR2(64 CHAR),
  content       CLOB,
  form_uuid     VARCHAR2(64 CHAR),
  resource_code VARCHAR2(64 CHAR)
);
-- Create/Recreate primary, unique and foreign key constraints 
alter table CD_PRINT_CONTENTS
  add primary key (UUID)
  using index;
  



